mapper.delete:

delete from t_clud_number
where studentId = #{studentId,jdbcType=INTEGER}
  and cludId = #{cludId,jdbcType=INTEGER}

mapper.update:

update t_clud_number
set joinTime = #{joinTime,jdbcType=TIMESTAMP},
  exitTime = #{exitTime,jdbcType=TIMESTAMP},
  cludPositionId = #{cludPositionId,jdbcType=INTEGER},
  joinApplication = #{joinApplication,jdbcType=TINYINT},
  exitApplication = #{exitApplication,jdbcType=VARCHAR},
  voteStatus = #{voteStatus,jdbcType=INTEGER}
where studentId = #{studentId,jdbcType=INTEGER}
  and cludId = #{cludId,jdbcType=INTEGER}

mapper.select:

select studentId, cludId, joinTime, exitTime, cludPositionId, joinApplication, exitApplication, 
voteStatus
from t_clud_number
where studentId = #{studentId,jdbcType=INTEGER}
  and cludId = #{cludId,jdbcType=INTEGER}

