mapper.update:
update t_exam_arrange
 examDate = #{examDate,jdbcType=DATE},
  examTime = #{examTime,jdbcType=INTEGER},
  examClassroomId = #{examClassroomId,jdbcType=INTEGER},
  examClrName = #{examClrName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, examSubId, examDate, examTime, examClassroomId, examClrName, examSubName,examYear
from t_exam_arrange
where id = #{id,jdbcType=INTEGER}
