mapper.update:
update t_exam_classroom_use
set useClrId = #{useClrId,jdbcType=INTEGER},
  useClrTime = #{useClrTime,jdbcType=INTEGER},
  useThrough = #{useThrough,jdbcType=INTEGER},
  useClrName = #{useClrName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, useClrId, useTime, useClrTime, useAuditTime, useThrough, useClrName
from t_exam_classroom_use
where useThrough = #{useThrough,jdbcType=INTEGER}
