mapper.update:
update t_exam_grade
  gradeStu = #{gradeStu,jdbcType=INTEGER},
  gradeLimit = #{gradeLimit,jdbcType=DATE},
  gradeJudge = #{gradeJudge,jdbcType=INTEGER},
  gradeViolate = #{gradeViolate,jdbcType=INTEGER},
  gradeReview = #{gradeReview,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and gradeStuId = #{gradeStuId,jdbcType=INTEGER}

mapper.select:
select id, gradeStuId, gradeSubId, gradeStu, gradeSubName, gradeLimit, gradeJudge, 
gradeAlterTime, gradeViolate, gradeReview, gradeMajor, gradeYear
from t_exam_grade
where id = #{id,jdbcType=INTEGER}
  and gradeStuId = #{gradeStuId,jdbcType=INTEGER}

select gradeSubId
from t_exam_grade
where id = #{id,jdbcType=INTEGER}
  and gradeStuId = #{gradeStuId,jdbcType=INTEGER}

select gradeSubName
from t_exam_grade
where id = #{id,jdbcType=INTEGER}
  and gradeStuId = #{gradeStuId,jdbcType=INTEGER}

select id, gradeStuId, gradeSubId, gradeStu, gradeSubName, gradeLimit, gradeJudge,
gradeAlterTime, gradeViolate, gradeReview, gradeMajor, gradeYear
from t_exam_grade
  gradeStuId = #{gradeStuId,jdbcType=INTEGER}
   and gradeMajor = #{gradeMajor,jdbcType=VARCHAR} 
   and gradeYear = #{gradeYear,jdbcType=INTEGER}
