mapper.delete:
delete from t_exam_violent
where id = #{id,jdbcType=INTEGER}
  and vioStuId = #{vioStuId,jdbcType=INTEGER}

mapper.update:
update t_exam_violent
set vioBehavior = #{vioBehavior,jdbcType=VARCHAR},
  vioContent = #{vioContent,jdbcType=VARCHAR},
  vioLevel = #{vioLevel,jdbcType=INTEGER},
  vioRegTime = #{vioRegTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
  and vioStuId = #{vioStuId,jdbcType=INTEGER}

mapper.select:
select id, vioStuId, vioBehavior, vioContent, vioLevel, vioRegTime
from t_exam_violent
where id = #{id,jdbcType=INTEGER}
  and vioStuId = #{vioStuId,jdbcType=INTEGER}
