mapper.insert:
insert into t_staff_salary (id, salaryAmount, staffId
  )
values (#{id,jdbcType=INTEGER}, #{salaryAmount,jdbcType=VARCHAR}, #{staffId,jdbcType=INTEGER}
  )

mapper.update:
update t_staff_salary
set salaryAmount = #{salaryAmount,jdbcType=VARCHAR},
  staffId = #{staffId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, salaryAmount, staffId
from t_staff_salary
where id = #{id,jdbcType=INTEGER}
