mapper.insert:

insert into t_stock (stockId, stockEquipment, stockProName, 
  stockTime, stockAmount, stockShelf
  )
values (#{stockId,jdbcType=INTEGER}, #{stockEquipment,jdbcType=VARCHAR}, #{stockProName,jdbcType=VARCHAR}, 
  #{stockTime,jdbcType=TIMESTAMP}, #{stockAmount,jdbcType=INTEGER}, #{stockShelf,jdbcType=VARCHAR}
  )

mapper.select:

select stockId, stockEquipment, stockProName, stockTime, stockAmount, stockShelf
from t_stock
where stockId = #{stockId,jdbcType=INTEGER}

