mapper.insert:
insert into t_student_code (id, stuInstitution, stuMajor, 
  stuClass, stuEnteredtime, stuSource
  )
values (#{id,jdbcType=INTEGER}, #{stuInstitution,jdbcType=VARCHAR}, #{stuMajor,jdbcType=VARCHAR}, 
  #{stuClass,jdbcType=VARCHAR}, #{stuEnteredtime,jdbcType=DATE}, #{stuSource,jdbcType=VARCHAR}
  )

mapper.select:
select id, stuInstitution, stuMajor, stuClass, stuEnteredtime, stuSource
from t_student_code
where id = #{id,jdbcType=INTEGER}
